import React, { useEffect, useState } from 'react'
import { View, Text, StyleSheet, TouchableWithoutFeedback, Button, TextInput, TouchableOpacity, TouchEvent } from 'react-native';


export default function Chat({ socket, username, room }) {
    const [click, setClick] = useState(1)
    const [currentMessage, setCurrentMessage] = useState("");
    const [messageList, setMessageList] = useState([]);
    const [boxes, setBoxes] = useState(
        [{ "name": "", "value": "" }, { "name": "", "value": "" }, { "name": "", "value": "" },
        { "name": "", "value": "" }, { "name": "", "value": "" }, { "name": "", "value": "" },
        { "name": "", "value": "" }, { "name": "", "value": "" }, { "name": "", "value": "" }]);
    // // const [winner, setWinner] = useState(null);
    // const [showalert, setShowalert]= useState(false)
    // const [winPosition, setWinPosition] = useState([
    //     [0, 1, 2], [3, 4, 5], [6, 7, 8],
    //     [0, 3, 6], [1, 4, 7], [2, 5, 8],
    //     [0, 4, 8], [2, 4, 6],
    // ]);
    const handleClick = ((index) => {
        const boxValue = [...boxes]
        
      

        //boxValue[index].value = "O"
        if (click % 2 === 0) {

            boxValue[index].value = "X";
            setClick(click + 1);
            console.log("Click",click);

        }
        else {
            boxValue[index].value = "O";
            setClick(click + 1);
        }

        
       // console.log("tictacdata",tictacdata);
        //const boxValue2 = [...boxes]
        //const Values = [...boxes]


    })

    useEffect(() => {
            socket.on("recived_tictoc", (data) => {
            console.log("Data", data);
            // const boxValue2 = [...boxes]
            // console.log("boxValue2", boxValue2);
            const dataValue = data.tic_tac_list
            console.log("setbocx", dataValue);
            setBoxes(dataValue);
        
            setClick(data.click_Number);
        })

    }, [socket]);
    useEffect(()=>{
        console.log("Click",click);
        const tictacdata = {
            room: room,
            author: username,
            time: new Date(Date.now()).getHours() +
                ":" +
                new Date(Date.now()).getMinutes(),
            tic_tac_list: boxes,
            click_Number: click,
            // position: index
        };
        socket.emit("send_tictactoe", tictacdata);
    }, [click])
    return (
        <View style={styles.mainContainer}>
            <View style={styles.container}>
                <Text style={styles.useNameText}>{username}</Text>
                <View style={{ flexDirection: "row", flexWrap: "wrap" }}>
                    {
                        boxes.map((s1, index) => <View style={{ flexBasis: "30%", borderRadius: 3, backgroundColor: "yellow", borderWidth: 3, textAlign: "center" }} key={index}>
                            <TouchableOpacity style={{
                                height: 30,
                                fontSize: 40,
                                textAlign: "center",
                                marginLeft: 20,
                                marginTop: 15,


                            }}
                                onPress={() => handleClick(index, s1)}
                            >
                                <Text>{s1.value}</Text>

                            </TouchableOpacity>

                        </View>)
                    }
                </View>
            </View>
        </View>
    )
}


const styles = StyleSheet.create({
    mainContainer: {

        marginTop: 100,
        marginBottom: 10,
        marginRight: 120,
        marginLeft: 120,
        alignItems: 'center',
        justifyContent: 'center'
    },
    container: {
        marginBottom: 50,
        backgroundColor: '#fff',
        alignItems: 'center',
        justifyContent: 'center',
    },
    useNameText: {
        color: "red",
        fontSize: 25,
    },

});
