import React, { useEffect, useState } from 'react'
import { View, Text, StyleSheet, TouchableWithoutFeedback, Button, TextInput, TouchableOpacity } from 'react-native';


export default function Chat({ socket, username, room }) {
    const [currentMessage, setCurrentMessage] = useState("");
    const [messageList, setMessageList] = useState([]);
    const [boxes, setBoxes] = useState(
        [{ "name": "", "value": "" }, { "name": "", "value": "" }, { "name": "", "value": "" },
        { "name": "", "value": "" }, { "name": "", "value": "" }, { "name": "", "value": "" },
        { "name": "", "value": "" }, { "name": "", "value": "" }, { "name": "", "value": "" }]);
    // // const [winner, setWinner] = useState(null);
    // const [showalert, setShowalert]= useState(false)
    const [winPosition, setWinPosition] = useState([
        [0, 1, 2], [3, 4, 5], [6, 7, 8],
        [0, 3, 6], [1, 4, 7], [2, 5, 8],
        [0, 4, 8], [2, 4, 6],
    ]);
    const handleClick = ((index, messageContent) => {
        const boxValue = [...boxes]
        console.log("boxValue", boxValue);
        const obj = boxValue[index]
        console.log("obj", obj);

        boxValue[index].value = "X"

        const tictacdata = {
            room: room,
            author: username,
            time: new Date(Date.now()).getHours() +
                ":" +
                new Date(Date.now()).getMinutes(),
            tic_tac_list: boxValue,
            position: index
        };
        socket.emit("send_tictactoe", tictacdata);
        const boxValue2 = [...boxes]
        boxValue2[index].value = "O"
        setBoxes(boxValue2);
        const Values = [...boxes]
        for (let i = 0; i < Values.length; i++) {
            if (Values[0].value === "O" && Values[1].value === "O" && Values[2].value === "O") {
                alert("You are a winner")
                break;
            }

            else if (Values[3].value === "O" && Values[4].value === "O" && Values[5].value === "O") {
                alert("You are a winner")
                break;
            }
            else if (Values[6].value === "O" && Values[7].value === "O" && Values[8].value === "O") {
                alert("You are a winner")
                break;
            }
            else if (Values[0].value === "O" && Values[3].value === "O" && Values[6].value === "O") {
                alert("You are a winner")
                break;
            }
            else if (Values[1].value === "O" && Values[4].value === "O" && Values[7].value === "O") {
                alert("You are a winner")
                break;
            }
            else if (Values[2].value === "O" && Values[5].value === "O" && Values[8].value === "O") {
                alert("You are a winner")
                break;
            }
            else if (Values[0].value === "O" && Values[4].value === "O" && Values[8].value === "O") {
                alert("You are a winner")
                break;
            }
            else if (Values[2].value === "O" && Values[4].value === "O" && Values[6].value === "O") {
                alert("You are a winner")
                break;
            }
        }

    })

    useEffect(() => {


        socket.on("recived_tictoc", (data) => {
            console.log("Data", data);
            const boxValue2 = [...boxes]
            boxValue2[data.position].value = "X"
            setBoxes(boxValue2);

        })

    }, [socket]);
    return (
        <View style={styles.mainContainer}>
            <View style={styles.container}>
                <Text style={styles.useNameText}>{username}</Text>
                <View style={{ flexDirection: "row", flexWrap: "wrap" }}>
                    {
                        boxes.map((s1, index) => <View style={{ flexBasis: "30%", borderRadius: 3, backgroundColor: "yellow", borderWidth: 3, padding: 25, textAlign: "center" }} key={index}>
                            <TouchableOpacity style={{
                                height: 30,
                                padding: 20,
                                color:"red",
                            }}
                                onPress={() => handleClick(index, s1)}
                            >
                               <Text>{s1.value}</Text> 
                            </TouchableOpacity>

                        </View>)
                    }
                </View>
            </View>
        </View>
    )
}


const styles = StyleSheet.create({
    mainContainer: {
        flex: 1,
        marginTop: 100,
        marginBottom: 10,
        marginRight: 120,
        marginLeft: 120,
        alignItems: 'center',
        justifyContent: 'center'
    },

    chatheader: {

        alignItems: "center",
        fontSize: 20,
    },
    chatbody: {
        marginLeft: 30,
        marginRight: 30,
        height: (450 - (70 + 70)),
        width: 450,
        border: 2,
        backgroundColor: "lemonchiffon",
        position: "relative",
    },
    chatfooter: {
        marginLeft: 30,
        marginRight: 30,
        width: 450,
        border: 2,
        backgroundColor: "white",
        position: "relative",
    },
    useNameText: {
        color: "red",
        fontSize: 40,
    },
    textInput: {
        padding: 4,
        borderColor: "black",
        borderWidth: 2,
        marginBottom: 3,
    },
    buttonTab: {
        marginBottom: 10,
        width: 400,
    },
    container: {
        flex: 1,
        marginTop: 15,
        marginBottom: 50,
        backgroundColor: '#fff',
        alignItems: 'center',
        justifyContent: 'center',
    },
    headerText: {
        fontSize: 40,
        fontWeight: "bold"
    },
    messageContent: {
        color: "black",
        fontSize: 25,
        marginRight: 20,
        marginLeft: 20,
    },
    messageMeta: {
        color: "black",
        marginRight: 20,
        marginLeft: 20,
    }
})
